/** * */ package com.introspect.test; /** * @author 叶昭良 * @time 2015年3月21日下午2:10:39 * @version com.introspect.testStudent V1.0 */ //据说下面是符合javaBeans设计思想的普通类 public class Student { //字段私有化 ，使用BeansInfo必须是用id name num而不是id name num /*private String id; private String name; private int num;*/ private int id; private String name; private String num; //提供一个无参的构造函数 public Student() { } @Override public String toString() { StringBuilder studentInfo = new StringBuilder(); studentInfo.append(&quot;学生的id：&quot;).append(id).append(&quot;,姓名:&quot;).append(name).append( &quot;,学号是:&quot;).append(num); return studentInfo.toString(); } public int getid() { return id; } public void setid(int id) { this.id = id; } public String getname() { return name; } public void setname(String name) { this.name = name; } public String getnum() { return num; } public void setnum(String num) { this.num = num; } } ``` MyORM的修改 1： 修改select的id变为num即可 修改对应的代码段 2： 重载select方法 变成只接受一个class的函数，具体如下所示 ```java public static Object select(Class clazz) throws SQLException { int count = 0; //用于数据库表 String className = clazz.getSimpleName(); StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert sbSQL.append(&quot;select count(*) tb from &quot;).append(className); //获取到 ResultSet ResultSet rs = JDBCUtils.executeQuery(sbSQL.toString()); while(rs.next()) { count = rs.getInt(&quot;tb&quot;); System.out.println(&quot;总共有&quot;+count+&quot;个数据&quot;); } System.out.println(&quot;成功&quot;); return count; //先非泛型 再泛型的selectById }  最终的MyORM代码如下：
/** * 对象 Student * mysql表 Student * * 类 表对应！ 通过对象传递进数组，并利用内省机制，获得类中的所有属性！ * 并获得其中的值，然后组合成为一个sql语句，并执行JDBCUtils 来进行 * 增、删、修改、查等操作 * * 目的 ：对象 --mysql表 进行一一对应并且可以进行传递 * 简单的MyOrm.insert( * delete * select * update 等 * * 约束条件：主键必须为Id，且是int类型，自动递增 * 字段名必须和属性名一样 也就是类的属性和表的字段保持一致 * 表名字和类的名字一样 */ package com.introspect.test; import com.jdbc.test.*; import java.beans.BeanInfo; import java.beans.IntrospectionException; import java.beans.Introspector; import java.beans.PropertyDescriptor; import java.lang.reflect.InvocationTargetException; import java.lang.reflect.Method; import java.sql.ResultSet; import java.sql.SQLException; import java.util.*; //需要存储字段的 list 同时需要存储值的list // 最后组装成一条mysql语句 /** * @author 叶昭良 * @time 2015年3月4日下午8:57:46 * @version com.introspect.testMyORM V1.0 */ public class MyORM { /** * sql = insert into Person(fieldName) values(?,?..) * 1 2 3 4 * JDBCUTils.executeUpdate(sql,propValue) * @param args */ public static void insert(Object obj) throws SQLException { Class clazz = obj.getClass(); BeanInfo beanInfo = null; beanInfo = getBeanInfo(clazz); //用于数据库表 String className = clazz.getSimpleName(); PropertyDescriptor[] pro = beanInfo.getPropertyDescriptors(); List&lt;String&gt; listFieldName = new ArrayList&lt;String&gt;(); for(PropertyDescriptor temp:pro) { String propName = temp.getName(); if(!propName.equals(&quot;id&quot;) &amp;&amp; !propName.equals(&quot;class&quot;)) { //扣除 id和class这两个内部字段 listFieldName.add(propName); } } StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert sbSQL.append(&quot;insert into &quot;).append(className); //第二步 拼接 字段 String fieldNames = listFieldName.toString(); sbSQL.append(fieldNames.replace('[', '(').replace(']', ')')); //第三步 拼接 值 sbSQL.append(&quot; values&quot;); //第四步 拼接 添加问号 char[] paramMarkArray = new char[listFieldName.size()]; for(int i = 0 ; i &lt; listFieldName.size() ; i++) { paramMarkArray[i] = '?'; } sbSQL.append(Arrays.toString(paramMarkArray).replace('[', '(').replace (']', ')')); //最后一步 调用JDBCUtils的 executeUpdate List&lt;Object&gt; paramValues = new ArrayList&lt;Object&gt;(); for(String propName : listFieldName) { PropertyDescriptor propDesc = findPropertyDescriptor(propName, pro); Object propValue =null; //obj传入的类或者表名的对象 propValue = invoke(propDesc, obj); paramValues.add(propValue); } //最终过程 toString toArray的转换 JDBCUtils.executeUpdate(sbSQL.toString(), paramValues.toArray()); } /** * * @param clazz * @return 返回一个beanInfo对象 */ private static BeanInfo getBeanInfo(Class clazz) { BeanInfo beanInfo = null; try { beanInfo = Introspector.getBeanInfo(clazz); }catch(IntrospectionException e) { throw new RuntimeException(&quot;内省出错！&quot;+e.getMessage()); } return beanInfo; } /** * 从propDesc中找名字为propName的PropertyDescriptor * @param propName * @param prop * @return */ private static PropertyDescriptor findPropertyDescriptor(String propName, PropertyDescriptor[] prop) { for(PropertyDescriptor temp : prop) { if(temp.getName().equals(propName)) { return temp; } } return null; } /** * 执行某个字段的读方法 并返回 propValue * @param propDesc * @param obj * @return */ private static Object invoke(PropertyDescriptor propDesc,Object obj) { Object propValue = null; try { propValue = propDesc.getReadMethod().invoke(obj); } catch (IllegalAccessException | IllegalArgumentException | InvocationTargetException e) { // TODO Auto-generated catch block throw new RuntimeException(&quot;获取&quot;+propDesc.getName()+&quot;错误&quot;); } return propValue; } //------------------------insert 程序 结束---------------- /** * delete from className where id = ? * * * 删除clazz对应表中的字段id为id的值 delete(Person.class,5); * @param clazz * @param id */ public static void delete(Class clazz, int id) throws SQLException { BeanInfo beanInfo = null; beanInfo = getBeanInfo(clazz); //用于数据库表 String className = clazz.getSimpleName(); PropertyDescriptor[] pro = beanInfo.getPropertyDescriptors(); List&lt;String&gt; listFieldName = new ArrayList&lt;String&gt;(); StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert sbSQL.append(&quot;delete from &quot;).append(className).append(&quot; where id = ?&quot;); JDBCUtils.executeUpdate(sbSQL.toString(), id); } /** * select * from Person where id = 2; * * 获取clazz对应表中id为字段为id的对应航的 并且填充到对象中 * Person p1 = (Person)select(Person.class,2) * p1.getName(), p1.getAge(); * @param clazz * @param id * @return */ //----------------------delete 程序结束-------------------- public static Object select(Class clazz,String num) throws SQLException { Object b1 = null; b1 = getInstance(clazz); BeanInfo beanInfo = null; beanInfo = getBeanInfo(clazz); //用于数据库表 String className = clazz.getSimpleName(); PropertyDescriptor[] pro = beanInfo.getPropertyDescriptors(); List&lt;String&gt; listFieldName = new ArrayList&lt;String&gt;(); for(PropertyDescriptor temp:pro) { String propName = temp.getName(); if(!propName.equals(&quot;id&quot;) &amp;&amp; !propName.equals(&quot;class&quot;)) { //扣除 id和class这两个内部字段 listFieldName.add(propName); } } StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert sbSQL.append(&quot;select * from &quot;).append(className).append(&quot; where num = ?&quot;); //获取到 ResultSet ResultSet rs = JDBCUtils.executeQuery(sbSQL.toString(), num); if(!rs.next()) { System.out.println(&quot;当前版本没有&quot;+num+&quot;的信息&quot;); // return; }else { System.out.println(&quot;当前版本有&quot;+num+&quot;的信息&quot;); for(String propName : listFieldName) { PropertyDescriptor propDesc = findPropertyDescriptor(propName, pro); //obj传入的类或者表名的对象 invoke(propDesc, b1,rs.getObject(propName)); //invoke(propDesc, b1,rs.getString(propName)); } } //设置num字段 PropertyDescriptor propDesc1 = findPropertyDescriptor(&quot;num&quot;, pro); invoke(propDesc1, b1,num); System.out.println(&quot;成功&quot;); return b1; //先非泛型 再泛型的selectById } public static Object select(Class clazz) throws SQLException { int count = 0; //用于数据库表 String className = clazz.getSimpleName(); StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert sbSQL.append(&quot;select count(*) tb from &quot;).append(className); //获取到 ResultSet ResultSet rs = JDBCUtils.executeQuery(sbSQL.toString()); while(rs.next()) { count = rs.getInt(&quot;tb&quot;); System.out.println(&quot;总共有&quot;+count+&quot;个数据&quot;); } System.out.println(&quot;成功&quot;); return count; //先非泛型 再泛型的selectById } private static Object getInstance(Class clazz) { Object b1 = null; try { b1 = clazz.newInstance(); } catch (InstantiationException | IllegalAccessException e) { // TODO Auto-generated catch block e.printStackTrace(); } return b1; } //private static void invoke(PropertyDescriptor propDesc,Object obj,String value) private static void invoke(PropertyDescriptor propDesc,Object obj,Object value) { //有问题 因为 /*try { propDesc.getWriteMethod().invoke(obj,value); } catch (IllegalAccessException | IllegalArgumentException | InvocationTargetException e) { // TODO Auto-generated catch block throw new RuntimeException(&quot;获取&quot;+propDesc.getName()+&quot;错误&quot;); } */ try { Method methodSet= propDesc.getWriteMethod(); if(methodSet !=null) { methodSet.invoke(obj,value); } } catch (IllegalAccessException | IllegalArgumentException | InvocationTargetException e) { // TODO Auto-generated catch block throw new RuntimeException(&quot;获取&quot;+propDesc.getName()+&quot;错误&quot;); } } //------------------select 结束------------------ // update Person set age='' where id=4; /** * 设置id= id的对象 ， 他的年龄为。。。 也可以名字 ，具体修改update 语句 * @param clazz * @param newValue 设置年龄的新值 * @param id 设置某个id * @throws SQLException */ public static void update(Class clazz,Object newValue,int id) throws SQLException { BeanInfo beanInfo = null; beanInfo = getBeanInfo(clazz); //用于数据库表 String className = clazz.getSimpleName(); PropertyDescriptor[] pro = beanInfo.getPropertyDescriptors(); List&lt;String&gt; listFieldName = new ArrayList&lt;String&gt;(); for(PropertyDescriptor temp:pro) { String propName = temp.getName(); if(!propName.equals(&quot;id&quot;) &amp;&amp; !propName.equals(&quot;class&quot;)) { //扣除 id和class这两个内部字段 listFieldName.add(propName); } } StringBuilder sbSQL = new StringBuilder(); //第一步 拼接 insert //可以设置 update 那个字符串 比如 String temp = &quot;age&quot; sbSQL.append(&quot;update &quot;).append(className).append(&quot; set age = &quot;).append(newValue).append(&quot;&quot; + &quot; where id = ?&quot;); JDBCUtils.executeUpdate(sbSQL.toString(), id); } }  关于JDBCUtils的片段：请参考http://www.rupeng.com/forum/thread-44526-1-1.html 测试新版本MyORM的有效性
