#!/usr/bin/env python &#34;&#34;&#34;Simple HTTP Server With Upload. This module builds on BaseHTTPServer by implementing the standard GET and HEAD requests in a fairly straightforward manner. &#34;&#34;&#34; __version__ = &#34;0.1&#34; __all__ = [&#34;SimpleHTTPRequestHandler&#34;] __author__ = &#34;bones7456&#34; __home_page__ = &#34;http://li2z.cn/&#34; import os import posixpath import BaseHTTPServer import urllib import cgi import shutil import mimetypes import re try: from cStringIO import StringIO except ImportError: from StringIO import StringIO class SimpleHTTPRequestHandler(BaseHTTPServer.BaseHTTPRequestHandler): &#34;&#34;&#34;Simple HTTP request handler with GET/HEAD/POST commands. This serves files from the current directory and any of its subdirectories. The MIME type for files is determined by calling the .guess_type() method. And can reveive file uploaded by client. The GET/HEAD/POST requests are identical except that the HEAD request omits the actual contents of the file. &#34;&#34;&#34; server_version = &#34;SimpleHTTPWithUpload/&#34; + __version__ def do_GET(self): &#34;&#34;&#34;Serve a GET request.&#34;&#34;&#34; f = self.send_head() if f: self.copyfile(f, self.wfile) f.close() def do_HEAD(self): &#34;&#34;&#34;Serve a HEAD request.&#34;&#34;&#34; f = self.send_head() if f: f.close() def do_POST(self): &#34;&#34;&#34;Serve a POST request.&#34;&#34;&#34; r, info = self.deal_post_data() print r, info, &#34;by: &#34;, self.client_address f = StringIO() f.write(&#39;&lt;!DOCTYPE html PUBLIC &#34;-//W3C//DTD HTML 3.2 Final//EN&#34;&gt;&#39;) f.write(&#34;&lt;html&gt;
&lt;title&gt;Upload Result Page&lt;/title&gt;
&#34;) f.write(&#34;&lt;body&gt;
&lt;h2&gt;Upload Result Page&lt;/h2&gt;
&#34;) f.write(&#34;&lt;hr&gt;
&#34;) if r: f.write(&#34;&lt;strong&gt;Success:&lt;/strong&gt;&#34;) else: f.write(&#34;&lt;strong&gt;Failed:&lt;/strong&gt;&#34;) f.write(info) f.write(&#34;&lt;br&gt;&lt;a href=\&#34;%s\&#34;&gt;back&lt;/a&gt;&#34; % self.headers[&#39;referer&#39;]) f.write(&#34;&lt;hr&gt;&lt;small&gt;Powered By: bones7456, check new version at &#34;) f.write(&#34;&lt;a href=\&#34;http://li2z.cn/?s=SimpleHTTPServerWithUpload\&#34;&gt;&#34;) f.write(&#34;here&lt;/a&gt;.&lt;/small&gt;&lt;/body&gt;
&lt;/html&gt;
&#34;) length = f.tell() f.seek(0) self.send_response(200) self.send_header(&#34;Content-type&#34;, &#34;text/html&#34;) self.send_header(&#34;Content-Length&#34;, str(length)) self.end_headers() if f: self.copyfile(f, self.wfile) f.close() def deal_post_data(self): boundary = self.headers.plisttext.split(&#34;=&#34;)[1] remainbytes = int(self.headers[&#39;content-length&#39;]) line = self.rfile.readline() remainbytes -= len(line) if not boundary in line: return (False, &#34;Content NOT begin with boundary&#34;) line = self.rfile.readline() remainbytes -= len(line) fn = re.findall(r&#39;Content-Disposition.*name=&#34;file&#34;; filename=&#34;(.*)&#34;&#39;, line) if not fn: return (False, &#34;Can&#39;t find out file name...&#34;) path = self.translate_path(self.path) fn = os.path.join(path, fn[0]) while os.path.exists(fn): fn += &#34;_&#34; line = self.rfile.readline() remainbytes -= len(line) line = self.rfile.readline() remainbytes -= len(line) try: out = open(fn, &#39;wb&#39;) except IOError: return (False, &#34;Can&#39;t create file to write, do you have permission to write?&#34;) preline = self.rfile.readline() remainbytes -= len(preline) while remainbytes &gt; 0: line = self.rfile.readline() remainbytes -= len(line) if boundary in line: preline = preline[0:-1] if preline.endswith(&#39;&#39;): preline = preline[0:-1] out.write(preline) out.close() return (True, &#34;File &#39;%s&#39; upload success!&#34; % fn) else: out.write(preline) preline = line return (False, &#34;Unexpect Ends of data.&#34;) def send_head(self): &#34;&#34;&#34;Common code for GET and HEAD commands. This sends the response code and MIME headers. Return value is either a file object (which has to be copied to the outputfile by the caller unless the command was HEAD, and must be closed by the caller under all circumstances), or None, in which case the caller has nothing further to do. &#34;&#34;&#34; path = self.translate_path(self.path) f = None if os.path.isdir(path): if not self.path.endswith(&#39;/&#39;): # redirect browser - doing basically what apache does self.send_response(301) self.send_header(&#34;Location&#34;, self.path + &#34;/&#34;) self.end_headers() return None for index in &#34;index.html&#34;, &#34;index.htm&#34;: index = os.path.join(path, index) if os.path.exists(index): path = index break else: return self.list_directory(path) ctype = self.guess_type(path) try: # Always read in binary mode. Opening files in text mode may cause # newline translations, making the actual size of the content # transmitted *less* than the content-length! f = open(path, &#39;rb&#39;) except IOError: self.send_error(404, &#34;File not found&#34;) return None self.send_response(200) self.send_header(&#34;Content-type&#34;, ctype) fs = os.fstat(f.fileno()) self.send_header(&#34;Content-Length&#34;, str(fs[6])) self.send_header(&#34;Last-Modified&#34;, self.date_time_string(fs.st_mtime)) self.end_headers() return f def list_directory(self, path): â€¦

